mapper.update:
UPDATE task SET
taskname = "${taskname}", owner = "${owner}", modifier = "${modifier}", updatetime = "${updatetime}"
WHERE taskid = #{taskid}

UPDATE task SET
prepared = 1
WHERE taskid = #{_parameter}

UPDATE task SET
taskdone = 1, taskscore = #{taskscore}
WHERE taskid = #{taskid}

UPDATE task SET
deleted = 1, modifier = "${modifier}", updatetime = "${updatetime}"
WHERE taskid = #{taskid}
